            arr[i] = new ArrayList<>();}
        if(flag) return 0;
        for(int i = 0 ; i < queries.length ; i++){
            for(int j = queries[i][0] ; j <= queries[i][1] ; j++){
                arr[j].add(queries[i][2]);
            }
            if(check(arr , nums)) return (i+1);
        }
        return -1;
    }

    boolean check(List<Integer> arr[] , int nums[]){
        boolean t = true;
        for(int i = 0 ; i < nums.length ; i++){
            if(nums[i] == 0) continue;
            if(!isSubsetSum(arr[i] , nums[i])) return false;
        }
        return true;
    }
     public boolean isSubsetSum(List<Integer> arr, int K) {
        int n = arr.size();
        boolean[] dp = new boolean[K + 1];
        dp[0] = true; // Base case

        for (int num : arr) {
            for (int j = K; j >= num; j--) { // Traverse backwards
                dp[j] |= dp[j - num];
            }
        }
        return dp[K];
    }
}

