class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<vector<bool>> visit(nums.size(), vector<bool>(1050));
        bool allZ = true;
        for (int i = 0; i < nums.size(); ++i) {
            allZ &= (nums[i] == 0);
            visit[i][0] = true;
        }

        if (allZ) return 0;

        for (int k = 0; k < queries.size(); ++k) {
            auto& q = queries[k];
            for (int i = q[0]; i <= q[1]; ++i) {
                for (int j = nums[i]; j - q[2] >= 0; --j) {
                    if (visit[i][j - q[2]]) {
                        visit[i][j] = true;
                    }
                }
            }
            bool p = true;
            for (int i = 0; i < nums.size() && p; ++i) {
                 p = visit[i][nums[i]];
            }
            if (p) return k + 1;
        }
        return -1;
    }
};

