class Solution {
public:
    bool fn(vector<int> nums, vector<vector<int>>& queries,int mid){
        int n = nums.size();
        map<int,vector<int>> m;
        for(int i=0;i<=mid;i++){
            int a = queries[i][0];
            int b = queries[i][1];
            for(int j=a;j<=b;j++){
                m[j].push_back(queries[i][2]);
            }
        }
        for(int j=0;j<n;j++){
            if(m.find(j)==m.end()){
                if(nums[j]!=0){
                    return false;
                }
            }
            else{
                int t = nums[j];
                if(t==0){
                    continue;
                }
                vector<bool> dp(t+1,false);
                dp[0] = true;
                for(auto &x:m[j]){
                    for(int s= t;s>=x;s--){
                        if(dp[s-x]){
                            dp[s] = true;
                        }
                    }
                }
                if(dp[t]==0) return false;
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int low =0;
        int high = m-1;
        int res = -1;
        int ct=0;
        for(int i=0;i<n;i++){
            if(nums[i]==0) ct++;
        }
        if(ct==n) return 0;
        while(low<=high){
            int mid = low+(high-low)/2;
            // cout<<"mid "<<mid<<endl;
            if(fn(nums,queries,mid)){
                res = mid+1;
                high = mid-1;
            }
            else{
                low = mid+1;
            }

        }
        return res;
    }
};

