class Solution {
public:
    int minI(const vector<vector<int>>& nums, int target) {
        int n = nums.size();
        vector<int> dp(target + 1, INT_MAX);
        dp[0] = 0;

        int minIndex = INT_MAX;

        for (int i = 0; i < n; ++i) {
            for (int j = target; j >= nums[i][0]; --j) {
                if (dp[j - nums[i][0]] != INT_MAX) {
                    dp[j] = min(dp[j], i + 1);
                    if (j == target) {
                        minIndex = min(minIndex, dp[j]);
                    }
                }
            }
        }

        return minIndex == INT_MAX ? -1 : minIndex - 1;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = -1, n = nums.size();
        for(int i=0; i<n; i++){
            if(nums[i] == 0) continue;
            vector<vector<int>> v;
            for(int j=0; j<queries.size(); j++){
                if(i>=queries[j][0] and i<=queries[j][1]) v.push_back({queries[j][2], j});
            }
            if(minI(v, nums[i]) == -1) return -1;
            ans = max(ans, v[minI(v, nums[i])][1]);
        }
        return ans+1;
    }
};

