class Solution {
private:
    bool solve(vector<int>&ele, vector<vector<int>>&dp, int i, int j){
        int m = dp.size(), n = dp[0].size();
        if(i==m)return false;
        if(dp[i][j]!=-1)return dp[i][j];
        if(ele[i]==j)return dp[i][j] = 1;
        dp[i][j] = solve(ele,dp,i+1,j);
        if(dp[i][j]!=1 && ele[i]<j){
            dp[i][j] = solve(ele,dp,i+1,j-ele[i]);
        }
        return dp[i][j];
    }
    bool check_sum(int sum, vector<int>ele){
        int n = ele.size();
        if(n==0)return false;
        vector<vector<int>>dp(n,vector<int>(sum+1,-1));
        return solve(ele,dp,0,sum);
    }
    bool possible(vector<int>& nums, vector<vector<int>>& queries, int k){
        int n = nums.size(), q = queries.size();
        vector<vector<int>> mat(n);
        for(int i =0; i<k; i++){
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for(int j = l; j<=r; j++){
                mat[j].push_back(val);
            }
        }
        for(int i=0; i<n; i++){
            if(nums[i]!=0){
                if(!check_sum(nums[i],mat[i]))return false;
            }
        }
        return true;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        int l = 0, r = q, mid;
        // F F F T T T -- first T
        while(l<r){
            mid = (l+r)/2;
            if(possible(nums,queries,mid)){
                r = mid;
            }
            else l = mid+1;
        }
        return possible(nums,queries,l)? l:-1;

    }
};

