class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& operations) {
        int left = 0, right = operations.size(), result = -1;
        while (left <= right) {
            int mid = (left + right) / 2;
            if (isPossible(nums, operations, mid)) {
                result = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        return result;
    }

private:
    bool isPossible(vector<int>& nums, vector<vector<int>>& operations, int timeLimit) {
        unordered_map<int, vector<int>> reductions;
        unordered_map<int, int> maxReduction;

        for (int i = 0; i < timeLimit; i++) {
            int leftIndex = operations[i][0], rightIndex = operations[i][1], value = operations[i][2];
            for (int j = leftIndex; j <= rightIndex; j++) {
                reductions[j].push_back(value);
                maxReduction[j] += value;
            }
        }

        for (int i = 0; i < nums.size(); i++) {
            if (nums[i] == 0 || nums[i] == maxReduction[i]) continue;
            if (nums[i] > maxReduction[i]) return false;

            int targetValue = nums[i];
            vector<int>& availableReductions = reductions[i];
            vector<bool> dp(targetValue + 1, false);
            dp[0] = true;

            for (int reductionValue : availableReductions) {
                for (int j = targetValue; j >= reductionValue; j--) {
                    dp[j] = dp[j] || dp[j - reductionValue];
                }
            }

            if (!dp[targetValue]) return false;
        }

        return true;
    }
};

