class Solution {
public:
    bool fun(int ind, int target, vector<int>& arr, vector<vector<int>>& dp) {
        if (target == 0) return true;
        if (ind < 0) return false;
        if (dp[ind][target] != -1) return dp[ind][target];

        bool no = fun(ind - 1, target, arr, dp);
        bool taken = false;
        if (arr[ind] <= target) 
            taken = fun(ind - 1, target - arr[ind], arr, dp);

        return dp[ind][target] = (no || taken);
    }

    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        map<int, vector<int>> m;
        int n = a.size();
        int ans = 0;
        int sum = accumulate(a.begin() , a.end() , 0ll);
        if(sum == 0)return 0;
        for (auto &x : q) {
            ans++;
            for (int i = x[0]; i <= x[1]; i++) {
                m[i].push_back(x[2]);
            }
            int f = 0;
            for (int i = 0 ; i < n ; i++) {
                int target = a[i];
                vector<vector<int>> dp(m[i].size(), vector<int>(target + 1, -1));

                if (!fun(m[i].size() - 1, target, m[i], dp)) {
                    f = 1;
                    break;
                }
            }
            if(f == 0) return ans;
        }

        return -1;

    }
};

