class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        vector<bitset<1001>> dp(n);

        for (int i = 0; i < n; i++) {
            dp[i].set(0);
        }

        if (all_of(nums.begin(), nums.end(), [](int x) { return x == 0; })) return 0;

        for (int k = 0; k < q.size(); k++) {
            applyQuery(dp, nums, q[k]);

            if (isAchieved(dp, nums)) return k + 1;
        }
        return -1;
    }

private:
    void applyQuery(vector<bitset<1001>>& dp, vector<int>& nums, vector<int>& query) {
        int l = query[0], r = query[1], v = query[2];

        for (int i = l; i <= r; i++) {
            bitset<1001> temp;
            for (int j = 0; j < 1001 - v; j++) {
                if (dp[i][j]) {
                    temp.set(j + v);
                }
            }
            dp[i] |= temp;

            for (int p = nums[i] + 1; p < 1001; p++) {
                dp[i].reset(p);
            }
        }
    }

    bool isAchieved(const vector<bitset<1001>>& dp, const vector<int>& nums) {
        for (int i = 0; i < nums.size(); i++) {
            if (!dp[i][nums[i]]) {
                return false;
            }
        }
        return true;
    }
};

