from math import gcd
class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        n = len(nums)
        dp = [1 for _ in range(n)]
        masks = [ (1 << (target + 1)) - 1 for target in nums ]

        def all_satisfied():
            for j in range(n):
                if ((dp[j] >> nums[j]) & 1) == 0:
                    return False
            return True

        if all_satisfied():
            return 0

        for k, (l, r, v) in enumerate(queries, 1):
            for j in range(l, r + 1):
                dp[j] |= (dp[j] << v)
                dp[j] &= masks[j]
            if all_satisfied():
                return k
        return -1

