class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        def valid(memo, decVal):
            curIndex = len(memo)
            n = len(memo[0])
            memo.append([False for _ in range(n)])
            for j in range(n):
                # If we don't include nums[i-1]
                memo[curIndex][j] = memo[curIndex - 1][j]

                # If we include nums[i-1]
                if j - decVal >= 0:
                    memo[curIndex][j] = memo[curIndex][j] or memo[curIndex - 1][j - decVal]
            return memo[-1][-1]


        solved = 0
        for num in nums:
            if num == 0:
                solved += 1

        if solved == len(nums):
            return 0

        dp = []
        for num in nums:
            innerDP = [[False for _ in range(num + 1)]]
            innerDP[0][0] = True  # Empty subset sums to 0
            dp.append(innerDP)

        for i in range(len(queries)):
            l, r, val = queries[i]
            for index in range(l, r+1):
                if nums[index] == 0:
                    continue
                if valid(dp[index], val):
                    solved += 1
                    nums[index] = 0
                    if solved == len(nums):
                        return i + 1
        return -1

