class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<int> earliest(n, -1);

        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                earliest[i] = 0;
                continue;
            }

            vector<bool> dp(1001, false);
            dp[nums[i]] = true;
            for (int j = 0; j < q; j++) {
                vector<bool> newDp = dp;
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                bool inRange = (i >= l && i <= r);
                if (inRange) {
                    for (int v = 0; v <= 1000; v++) {
                        if (!dp[v]) continue;
                        if (v >= val) {
                            newDp[v - val] = true;
                        }
                    }
                }
                dp = move(newDp);
                if (dp[0]) {
                    earliest[i] = j + 1;
                    break;
                }
            }
        }

        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (earliest[i] == -1)
                return -1;
            ans = max(ans, earliest[i]);
        }
        return ans;
    }
};

