class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        min_k_required = [0] * n  # For indices already 0, k = 0.

        for i in range(n):
            A = nums[i]  # original value at index i
            # If the element is already 0, no queries are needed.
            if A == 0:
                min_k_required[i] = 0
                continue

            # Build a list of queries (as (global_index, v)) that affect index i.
            queries_for_i = []
            for k, (l, r, v) in enumerate(queries):
                if l <= i <= r:
                    queries_for_i.append((k, v))

            # If no query affects index i and A > 0, it's impossible.
            if not queries_for_i:
                return -1

            # Use DP to track reachable sums for index i.
            reachable = {0}
            earliest = None  # the minimal query count (k) at which A is reachable.

            for (k, v) in queries_for_i:
                # Make a copy of the current reachable set; skipping is always allowed.
                new_reachable = set(reachable)
                for s in reachable:
                    if s + v <= A:
                        new_reachable.add(s + v)
                reachable = new_reachable

                if A in reachable:
                    earliest = k + 1  # queries are 0-indexed; we need count.
                    break

            if earliest is None:
                # For index i, it's not possible to achieve exactly A.
                return -1
            else:
                min_k_required[i] = earliest

        return max(min_k_required)

