class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        cat = defaultdict(list)

        for j,q in enumerate(queries):
            l,r,val = q
            for k in range(l,r+1):
                cat[k].append([-val,j])

        #print(cat)

        dp = {}

        self.res = -1
        self.cat = None
        self.dp = {}
        self.curr = None

        def search(n,i):
            n,i = int(n),int(i)
            if i>=len(self.cat):
                return 1e9
            elif self.curr<=i:
                return self.curr

            res = 1e9

            for j in range(i,len(self.cat)):
                v,k = self.cat[j]
                n2 = int(n+v)

                if n2==0:
                    res = min(res,k)
                    continue
                elif n2<0:
                    continue
                else:
                    nextt = (n2,j+1)

                    if nextt in self.dp:
                        res0 = self.dp[nextt]
                    else:
                        res0 = search(n2,j+1)
                    res = min(res,res0)

            self.dp[(n,i)] = res
            self.curr = min(self.curr,res)
            return res


        for i,n in enumerate(nums):
            self.cat = cat[i]

            if n==0:
                continue
            elif len(self.cat)==0 and n>0:
                return -1 
            self.dp = {}
            self.curr = 1e9
            res0 = search(n,0)

            #print(i,n,res0)

            if res0>=1000:
                return -1
            else:
                self.res = max(self.res,res0)

        return self.res+1

