#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<bitset<1001>> dp(n);

        for (int j = 0; j < n; j++) {
            dp[j].set(0);
        }

        bool zeroFull = true;
        for(int i : nums)
            if(i!=0){
                zeroFull = false;
                break;
            }

        if (zeroFull) return 0;

        for (int k = 0; k < queries.size(); k++) {
            int l = queries[k][0], r = queries[k][1], v = queries[k][2];

            for (int j = l; j <= r; j++) {
                bitset<1001> shifted;
                for (int i = 0; i < 1001 - v; i++) {
                    if (dp[j][i]) {
                        shifted.set(i + v);
                    }
                }
                dp[j] |= shifted;

                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp[j].reset(pos);
                }
            }

            zeroFull = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j][nums[j]]) {
                    zeroFull = false;
                    break;
                }
            }

            if (zeroFull) return k + 1;
        }
        return -1;
    }
};

