class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        prefix = [[] for i in range(len(nums))]
        for i, q in enumerate(queries):
            l,r,v = q
            for j in range(l,r+1):
                prefix[j].append((i+1,v))


        def koko(k):
            for i in range(len(nums)):
                s = []
                for j in range(len(prefix[i])):
                    if prefix[i][j][0] > k:
                        break
                    s.append(prefix[i][j][1])
                if nums[i] == 0:
                    continue
                if sum(s) < nums[i]:
                    return False

                dp = [0] * (nums[i]+1)
                dp[0] = True
                for v in s:
                    for x in range(nums[i],v-1,-1):
                        if dp[x-v]: dp[x] = True
                    if dp[nums[i]]:
                        break
                if not dp[nums[i]]:
                    return False 
            return True

        l,r = 0,len(queries)
        res = -1
        while l <= r:
            m = (l + r)//2
            if koko(m):
                res = m
                r = m-1
            else:
                l = m+1
        return res

