class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<int> mn(nums.size(), -1);
        for (int i=0; i<nums.size(); i++) {
            if (nums[i]==0)
                mn[i] = 0;
        }
        vector<vector<int>> dp(nums.size(), vector<int>(1001,0));
        for (int i=0; i<queries.size(); i++) {
            vector<vector<int>> tmp(nums.size(), vector<int>(1001,0));
            for (int k=0; k<nums.size(); k++) {
                if (i==0)
                    tmp[k][0] = 1;
                else {
                    for (int j=0; j<1001; j++) {
                        tmp[k][j] = dp[k][j];
                    }
                }
            }
            for (int k=queries[i][0]; k<=queries[i][1]; k++) {
                if (i==0) {
                    tmp[k][queries[i][2]] = 1;
                }
                else {
                    for (int j=0; j<1001; j++) {
                        if (j-queries[i][2]>=0 && dp[k][j-queries[i][2]])
                            tmp[k][j] = 1;
                    }
                }
            }
            for (int k=0; k<nums.size(); k++) {
                if (tmp[k][nums[k]] == 1) {
                    // cout << i << " " << k << " yes" << endl; 
                    if (mn[k] == -1)
                        mn[k] = i+1;
                }
            }
            dp = tmp;
        }
        int ans = mn[0];
        for (int i=0; i<mn.size(); i++) {
            if (mn[i]==-1)
                return -1;
            ans = max(ans, mn[i]);
        }
        return ans;
    }
};

