#include <vector>
#include <bitset>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        // Check if a given prefix of queries (first k queries) can make nums a Zero Array.
        auto canAchieve = [&](int k) -> bool {
            for (int j = 0; j < n; j++) {
                int target = nums[j];
                // If no subtraction is needed, skip this index.
                if (target == 0) continue;

                // dp[s] == true means we can obtain sum s using queries affecting index j.
                bitset<1001> dp;
                dp.reset();
                dp.set(0, true);

                // Create a mask that keeps only sums up to 'target'
                bitset<1001> mask;
                mask.reset();
                for (int s = 0; s <= target; s++) {
                    mask.set(s, true);
                }

                // Process the first k queries
                for (int i = 0; i < k; i++) {
                    int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                    if(j >= l && j <= r) {
                        dp |= (dp << val);
                        dp &= mask; // Only keep sums up to target.
                    }
                }
                // If we cannot exactly reach target, then prefix k fails.
                if(!dp.test(target))
                    return false;
            }
            return true;
        };


        int lo = 0, hi = m + 1; 
        while(lo < hi) {
            int mid = lo + (hi - lo) / 2;
            if(canAchieve(mid))
                hi = mid;
            else
                lo = mid + 1;
        }
        return (lo == m + 1 ? -1 : lo);
    }
};

