class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        int[][] values = new int[n][m];
        int max = -1;

        for (int j = 0; j < m; j++) {
            for (int i = queries[j][0]; i <= queries[j][1]; i++) {
                values[i][j] = queries[j][2];
            }
        }

        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                continue;
            }
            int[] vals = values[i];
            int index = findMinimumHighestIndex(vals, nums[i]);
            if (index == -1) {
                return -1;
            }
            if (index > max) {
                max = index;
            }
        }


        return max + 1;
    }

    private int findMinimumHighestIndex(int[] values, int target) {
        int n = values.length;

        int[][] dp = new int[n + 1][target + 1];
        for (int[] row : dp) {
            Arrays.fill(row, Integer.MAX_VALUE);
        }
        dp[0][0] = -1;

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= target; j++) {
                // out
                dp[i][j] = dp[i - 1][j];
                // in
                if (j >= values[i - 1] && dp[i - 1][j - values[i - 1]] != Integer.MAX_VALUE) {
                    dp[i][j] = Math.min(dp[i][j], i - 1);
                }
            }
        }

        int index = dp[n][target];
        return index == Integer.MAX_VALUE ? -1 : index;
    }
}

