class Solution {
public:
    bool chk(const vector<int>& a) {
        for (int x : a)
            if (x != 0)
                return false;
        return true;
    }

    bool ss(int t, const vector<int>& c) {
        vector<bool> dp(t + 1, false);
        dp[0] = true;
        for (int coin : c)
            for (int s = t; s >= coin; s--)
                if (dp[s - coin])
                    dp[s] = true;
        return dp[t];
    }

    int minZeroArray(vector<int>& a, vector<vector<int>>& qu) {
        vector<int> v = a;
        if (chk(v))
            return 0;
        int n = v.size();
        int m = qu.size();
        for (int k = 1; k <= m; k++) {
            bool all = true;
            for (int i = 0; i < n; i++) {
                vector<int> c;
                for (int j = 0; j < k; j++)
                    if (qu[j][0] <= i && i <= qu[j][1])
                        c.push_back(qu[j][2]);
                if (!ss(v[i], c)) {
                    all = false;
                    break;
                }
            }
            if (all)
                return k;
        }
        return -1;

    }
};

