class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        non_zero = {}
        for i, num in enumerate(nums):
            if num == 0:
                continue 
            non_zero[i] = set([num])
        if len(non_zero) == 0:
            return 0
        for idx, query in enumerate(queries):
            l, r, val = query
            cp = dict(non_zero)
            for i in non_zero.keys():
                rem = False
                if i < l or i > r:
                    continue 
                new_set = set(non_zero[i])
                for s in non_zero[i]:
                    if s - val == 0:
                        del cp[i]
                        rem = True 
                        break 
                    new_set.add(s - val)
                if len(cp) == 0:
                    return idx + 1
                if not rem:
                    cp[i] = new_set
            non_zero = dict(cp)
        return -1

