class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        vector<vector<pair<int, int>>> s(n);

        for (int i = 0; i < q.size(); ++i) {
            int l = q[i][0], r = q[i][1], v = q[i][2];
            for (int j = l; j <= r; ++j) s[j].emplace_back(v, i);
        }

        int ans = 0;
        for (int i = 0; i < n; ++i) {
            int t = a[i];
            if (t == 0) continue;

            auto& l = s[i];
            if (l.empty()) return -1;

            vector<bool> dp(t + 1, false);
            dp[0] = true;
            int idx = -1, sum = 0;

            for (int j = 0; j < l.size(); ++j) {
                int v = l[j].first, o = l[j].second;
                sum += v;
                for (int k = t; k >= v; --k) if (dp[k - v]) dp[k] = true;
                if (sum >= t && dp[t]) { idx = o; break; }
            }

            if (idx == -1) return -1;
            ans = max(ans, idx + 1);
        }

        return ans;
    }
};

