class Solution {
private:
    bool helper(vector<vector<int>>& dp, int n, int ind, int sum,
                vector<int>& arr) {
        if (sum == 0) {
            return true;
        }
        if (ind == -1) {
            return false;
        }
        if (dp[ind][sum] != -1) {
            return dp[ind][sum];
        }
        int notpick = helper(dp, n, ind - 1, sum, arr);
        int pick = 0;
        if (arr[ind] <= sum) {
            pick = helper(dp, n, ind - 1, sum - arr[ind], arr);
        }
        return dp[ind][sum] = pick || notpick;
    }
    bool check0(vector<int>& arr, int sum, int n) {
        vector<vector<int>> dp(n, vector<int>(sum + 1, -1));
        sort(arr.begin(),arr.end());
        return helper(dp, n, n - 1, sum, arr);
    }
    bool bs(vector<int>& nums, vector<vector<int>>& q, int sz, int n) {
        multiset<int> st;
        vector<vector<int>> mark(n + 1);
        for (int i = 0; i <= sz; i++) {
            int l = q[i][0];
            int r = q[i][1];
            int val = q[i][2];
            mark[l].push_back(val);
            mark[r + 1].push_back(val*(-1));
        }

        for (int i = 0; i < n; i++) {
            int msz = mark[i].size();
            for (int j = 0; j < msz; j++) {
                if (mark[i][j] > 0) {
                    st.insert(mark[i][j]);
                } else {
                    auto it = st.find(abs(mark[i][j]));
                    if (it != st.end()) {
                        st.erase(it);
                    }

                }
            }
            vector<int> arr;
            for (auto it : st) {
                arr.push_back(it);
            }
            if (nums[i] == 0) {
                continue;
            }
            if (arr.empty()) {
                return false;
            }
            if (!check0(arr, nums[i], arr.size())) {
                return false;
            }
        }
        return true;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        bool found=false;
        for(int i=0;i<n;i++){
            if(nums[i]!=0){
                found=true;
                break;
            }
        }
        if(!found){
            return 0;
        }
        int m = q.size();
        int low = 0;
        int high = m - 1;
        int ans = -1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (bs(nums, q, mid, n)) {
                ans = mid + 1;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return ans;
    }
};

