class Solution {
   public int minZeroArray(int[] nums, int[][] queries) {

    	int n = nums.length;

    	ArrayList<int[]>[] lst = new ArrayList[n];

    	for(int i=0;i<n;i++){
    		lst[i] = new ArrayList<>();
    	}

    	int qnum= 0;
    	for(int q[] : queries){

    		qnum++;
    		int l = q[0];
    		int r = q[1];
    		int val = q[2];

			int x[] = {val,qnum};
    		for(int i=l;i<=r;i++){    			
    			lst[i].add(x);    			
    		}

    	}

    	int res[] = new int[n];
    	Arrays.fill(res, 0);

    	for(int i=0;i<n;i++){

    		if(nums[i]==0){
    			res[i]=0;
    			continue;
    		}


    		int minq = getMin(nums[i],lst[i]);

    		if(minq==-1)
    			return -1;

    		res[i] = minq;

    	}

    	int result = Arrays.stream(res).max().getAsInt();

    	return result;
    }


    private int getMin(int sum, ArrayList<int[]> lst) {

       int N = lst.size() ;
       boolean ar[]=new boolean[sum+1];

        ar[0]=true;        
        for(int i=0;i<N;i++){

        	int start  = lst.get(i)[0];
        	int qnum = lst.get(i)[1];

            for(int j=sum;j>=start;j--){        
                ar[j] = ar[j]  |ar[j-start];        
            }

            if(ar[sum]) return qnum;

        }

        return -1;
	}


}

