class Solution {
public:
    int minIndexCal(int target, const vector<pair<int, int>>& queries) {
        if (target == 0)
            return 0;

        const int INF = 1e9;
        vector<int> dp(target + 1, INF);
        dp[0] = 0;

        for (const auto& [queryIdx, weight] : queries) {
            for (int s = target - weight; s >= 0; s--) {
                if (dp[s] != INF) {
                    dp[s + weight] = 
                        min(dp[s + weight], queryIdx + 1);
                }
            }
        }
        return (dp[target] == INF ? -1 : dp[target]);
    }

    int minZeroArray(vector<int>& targets, vector<vector<int>>& queries) {
        int n = targets.size();
        vector<vector<pair<int, int>>> weights(n);
        for(int j=0; j<queries.size(); j++) {
            int l = queries[j][0];
            int r = queries[j][1];
            int w = queries[j][2];
            for(int i = l; i <= r; i++)
                weights[i].push_back({j, w});
        }

        int minIdex = 0;
        for(int i=0; i<n; i++) {
            int minIdxTarget = minIndexCal(targets[i], weights[i]);
            if(minIdxTarget == -1)
                return -1;
            minIdex = max(minIdex, minIdxTarget);
        }
        return minIdex;
    }
};

