class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& b) {
        int n = a.size();
        unordered_map<int, vector<bool>> memo;

        for (int i = 0; i < n; i++) {
            memo[i] = vector<bool>(1001, false);
            memo[i][0] = true;
        }

        bool allZero = true;
        for (int i = 0; i < n; i++) {
            if (a[i] != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        for (int idx = 0; idx < b.size(); idx++) {
            int l = b[idx][0];
            int r = b[idx][1];
            int v = b[idx][2];

            for (int j = l; j <= r; j++) {
                vector<bool> temp = memo[j];
                for (int pos = 1000 - v; pos >= 0; pos--) {
                    if (memo[j][pos]) {
                        temp[pos + v] = true;
                    }
                }
                memo[j] = temp;
                for (int pos = a[j] + 1; pos < 1001; pos++) {
                    memo[j][pos] = false;
                }
            }

            bool allReached = true;
            for (int i = 0; i < n; i++) {
                if (!memo[i][a[i]]) {
                    allReached = false;
                    break;
                }
            }
            if (allReached) return idx + 1;
        }
        return -1;
    }
};

