class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        if (isZeroArray(nums)) {
            return 0;
        }
        int low = 1;
        int high = queries.length;
        int result = -1;
        while (low <= high) {
            int middle = low + (high - low) / 2;
            if (isFeasible(middle, nums, queries)) {
                result = middle;
                high = middle - 1;
            } else {
                low = middle + 1;
            }
        }
        return result;
    }
    public boolean isFeasible(int k, int[] nums, int[][] queries) {
        for (int i = 0; i < nums.length; i++) {
            int target = nums[i];
            if (target == 0) {
                continue;
            }
            boolean[] dp = new boolean[target + 1];
            dp[0] = true;
            for (int j = 0; j < k; j++) {
                if (queries[j][0] <= i && i <= queries[j][1]) {
                    int v = queries[j][2];
                    for (int t = target; t >= v; t--) {
                        if (dp[t - v]) {
                            dp[t] = true;
                        }
                    }
                }
            }
            if (!dp[target]) {
                return false;
            }
        }
        return true;
    }
    public boolean isZeroArray(int[] nums) {
        for (int num : nums) {
            if (num != 0) {
                return false;
            }
        }
        return true;
    }
}

