class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        List<int[]>[] numOperations=new ArrayList[nums.length];
        for(int i=0;i<nums.length;i++){
            numOperations[i]=new ArrayList<>();
        } 
        for(int i=0;i<queries.length;i++){
            for(int k=queries[i][0];k<=queries[i][1];k++){
                numOperations[k].add(new int[]{queries[i][2],i});
            }
        }
        int ans=-1;
        for(int i=0;i<nums.length;i++){
            boolean[] myMap=new boolean[nums[i]+1];
            myMap[nums[i]]=true;
            if(nums[i]==0)continue;
            int curAns=0;
            for(int k=0;k<numOperations[i].size();k++){
                for(int j=1;j<=nums[i];j++){
                    if(myMap[j]==false)continue;
                    if(j<numOperations[i].get(k)[0])continue; 
                    if(j==numOperations[i].get(k)[0]){
                        myMap[0]=true;
                        curAns=numOperations[i].get(k)[1];
                        break;
                    }
                    myMap[j-numOperations[i].get(k)[0]]=true;
                } 
                if(myMap[0])break;
            } 
            if(myMap[0]==false){
                return -1; 
            }else{
                ans=Math.max(ans,curAns);
            }
        }
        return ans+1;
    }
}

