class Solution {
public:
    int helper2(int i, int sum, int index, vector<vector<int>>& queries, vector<vector<int>>& dp){
        int n = queries.size();

        if(sum == 0){
            return i;
        }
        else if(i == n){
            return INT_MAX;
        }

        if(dp[i][sum] != -1){
            return dp[i][sum];
        }

        int ans1 = helper2(i+1, sum, index, queries, dp);
        int ans2 = INT_MAX;

        auto query = queries[i];
        int l = query[0];
        int r = query[1];
        int val = query[2];

        if(index >= l && index <= r && sum >= val){
            ans2 = helper2(i+1, sum - val, index, queries, dp);
        }

        return dp[i][sum] = min(ans1, ans2);
    }

    int helper(int index, int num, vector<vector<int>>& queries){
        vector<vector<int>> dp(queries.size(), vector<int>(num + 1, -1));

        int ans = helper2(0, num, index, queries, dp);

        return ans == INT_MAX ? -1: ans;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        int maxK = -1;

        for(int i = 0; i < n; i++){
            int ans = helper(i, nums[i], queries);

            if(ans == -1) return -1;

            maxK = max(maxK, ans);

        }

        return maxK;
    }
};

