class Solution {
public:
    int knapsack (vector <int> &v, int tgt) {
        int q = v.size();
        vector <vector <bool>> dp(q, vector <bool> (tgt + 1));
        dp[0][0] = 1;
        for (int j = 1; j <= tgt; j ++) {
            if (v[0] == j) dp[0][j] = 1;
            else dp[0][j] = 0;
        }
        if (dp[0][tgt]) return 1;
        for (int i = 1; i < q; i ++) {
            for (int j = 0; j <= tgt; j ++) {
                dp[i][j] = dp[i - 1][j] | (j >= v[i] ? dp[i - 1][j - v[i]] : 0);
            }
            if (dp[i][tgt]) return i + 1;
        }
        return q + 1;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size(), ans = 0;
        for (int i = 0; i < n; i ++) {
            int tgt = nums[i];
            if (tgt == 0) continue;
            vector <int> v(q);
            for (int j = 0; j < q; j ++) {
                if (i >= queries[j][0] && i <= queries[j][1]) {
                    v[j] = queries[j][2];
                }
                else {
                    v[j] = 0;
                }
            }
            ans = max(ans, knapsack(v, tgt));
            if (ans > q) return -1;
        }
        return ans;
    }
};

