class Solution {
    public int minZeroArray(int[] values, int[][] operations) 
    {
        int size = values.length;
        BitSet[] bitSets = initializeBitSets(size);

        if(areAllZeros(values)) return 0;

        for(int index=0; index<operations.length;index++) 
        {
            applyOperation(bitSets, values, operations[index]);
            if(allTargetsMet(bitSets, values)) 
            {
                return index + 1;
            }
        }
        return -1;
    }

    private BitSet[] initializeBitSets(int size) 
    {
        BitSet[] bitSets = new BitSet[size];
        for(int i=0;i<size;i++) 
        {
            bitSets[i] = new BitSet(1001);
            bitSets[i].set(0, true);
        }
        return bitSets;
    }

    private boolean areAllZeros(int[] values) {
        for(int value : values) {
            if (value != 0) return false;
        }
        return true;
    }


    private void applyOperation(BitSet[] bitSets, int[] values, int[] operation) {
        int start = operation[0], end = operation[1], increment = operation[2];

        for(int i=start;i<=end;i++) 
        {
            BitSet shiftedSet = shiftLeft(bitSets[i], increment);
            bitSets[i].or(shiftedSet);
            limitBitSet(bitSets[i], values[i]);
        }
    }


    private BitSet shiftLeft(BitSet bitSet, int shift) 
    {
        BitSet shiftedSet = new BitSet(1001);
        for(int i=0;i+shift<1001;i++) 
        {
            if(bitSet.get(i)) 
            {
                shiftedSet.set(i + shift);
            }
        }
        return shiftedSet;
    }


    private void limitBitSet(BitSet bitSet, int maxLimit) 
    {
        for(int pos=maxLimit+1; pos<1001;pos++) 
        {
            bitSet.clear(pos);
        }
    }


    private boolean allTargetsMet(BitSet[] bitSets, int[] values) 
    {
        for(int i=0;i<values.length;i++) 
        {
            if(!bitSets[i].get(values[i])) 
            {
                return false;
            }
        }
        return true;
    }
}

