    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(); 
        vector<vector<int>> m(n, vector<int>(1002, 0));
        for(int i = 0; i<n; i++) m[i][0] = 1;
        int formed = 0;
        vector<int> done(n, 0);
        for(int i = 0; i<n; i++) if(nums[i]==0) {done[i] = 1; formed++;};
        if(formed==n) return 0;
        for(int j = 0; j<queries.size(); j++){
            auto l = queries[j][0]; 
            auto r = queries[j][1]; 
            auto v = queries[j][2]; 
            for(int i = l; i<=r; i++){
                if(done[i]) continue;
                auto can = form(m[i], v, nums[i]);
                if(can){
                    done[i] = 1;
                    formed++;
                    if(formed==n) return j+1;
                }
            }
        }
        return -1;
    }
};

