from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        if all(num == 0 for num in nums):
            return 0
        ops_per_index = [[] for _ in range(n)]
        for idx, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                ops_per_index[i].append((idx, val))
        low, high, answer = 1, len(queries), -1
        while low <= high:
            mid = (low + high) // 2
            if self.canTransformToZero(nums, ops_per_index, mid):
                answer = mid
                high = mid - 1
            else:
                low = mid + 1  
        return answer
    def canTransformToZero(self, nums: List[int], ops_per_index: List[List[tuple]], k: int) -> bool:
        for i in range(len(nums)):
            target = nums[i]
            dp = [False] * (target + 1)
            dp[0] = True
            for op_idx, val in ops_per_index[i]:
                if op_idx < k:
                    for s in range(target, val - 1, -1):
                        if dp[s - val]:
                            dp[s] = True
            if not dp[target]:
                return False
        return True

