class Solution {
public:

    bool coinChange(vector<int>& coins,int amount,int index,
                    vector<vector<bool>>& dp){
        if(amount == 0){
            return true;
        }
        if(index == coins.size() || amount<0){
            return false;
        }

        if(dp[index][amount] != 0){
            return dp[index][amount];
        }
        return (coinChange(coins,amount,index+1,dp) || 
            coinChange(coins,amount-coins[index],index+1,dp));
    }

    bool possible(vector<int>& nums, 
                vector<vector<int>>& queries,int limit){

        int n = nums.size();
        int q = queries.size();
        vector<vector<int>> range(n);
        for(int i = 0; i<limit; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            for(int j = l; j<=r; j++){
                range[j].push_back(queries[i][2]);
            }
        }

          for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target != 0 && range[i].empty()) return false;

        map<int, int> dp;
        dp[0] = 1; 
        for (int v : range[i]) {
            map<int, int> new_dp = dp;
            for (auto& [sum, _] : dp) {
                int new_sum = sum + v;
                if (new_sum <= target) {
                    new_dp[new_sum] = 1;
                }
            }
            dp = new_dp;
        }
       if (dp.find(target) == dp.end()) return false;
    }

    return true;
    }

bool bs(vector<int>& nums, vector<vector<bool>>& rec,int limit){
