import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean flag = true;
        for (int ele : nums) {
            if (ele != 0) {
                flag = false;
                break;
            }
        }
        if (flag) return 0;

        int left = 0, right = queries.length - 1, ret = -1;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (helper(mid, nums, queries)) {
                ret = mid + 1;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        return ret;
    }

    private boolean helper(int k, int[] nums, int[][] queries) {
        int n = nums.length;
        List<Integer>[] list = new ArrayList[n];
        for (int i = 0; i < n; i++) {
            list[i] = new ArrayList<>();
        }
        for (int i = 0; i <= k; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) {
                list[j].add(val);
            }
        }

        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) continue;
            boolean[] dp = new boolean[target + 1];
            dp[0] = true;
            for (int v : list[i]) {
                for (int s = target; s >= v; s--) {
                    if (dp[s - v]) {
                        dp[s] = true;
                    }
                }
            }
            if (!dp[target]) return false;
        }
        return true;
    }
}

