#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<unordered_set<int>> dp(n);

        // Initialize dp[j] with {0} for each index
        for (int j = 0; j < n; j++) {
            dp[j].insert(0);
        }

        // Check if already a zero array
        if (all_of(nums.begin(), nums.end(), [](int x) { return x == 0; })) {
            return 0;
        }

        // Process each query
        for (int k = 0; k < queries.size(); k++) {
            int l = queries[k][0], r = queries[k][1], val = queries[k][2];

            // Update possible values at each affected index
            for (int j = l; j <= r; j++) {
                unordered_set<int> newValues;
                for (int x : dp[j]) {
                    if (x + val <= 1000) newValues.insert(x + val);
                }
                dp[j].insert(newValues.begin(), newValues.end());

                // Remove values exceeding nums[j]
                for (auto it = dp[j].begin(); it != dp[j].end();) {
                    if (*it > nums[j]) it = dp[j].erase(it);
                    else ++it;
                }
            }

            // Check if nums can be reduced to zero
            bool allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (dp[j].find(nums[j]) == dp[j].end()) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;
        }

        return -1;
    }
};

