class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        # coded by Arun Pranav A T
        n = len(nums)
        dp = [set([0]) for _ in range(n)]
        if all(num == 0 for num in nums):
            return 0
        for ind, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                dp[i] |= {val + num for num in dp[i] if val + num <= 1000}
                dp[i] = {num for num in dp[i] if num <= nums[i]}
            if all(nums[i] in dp[i] for i in range(n)):
                return ind + 1
        return -1

