class Solution {
public:
    int helper(int i, int val, vector<pair<int, int>>& v,
               vector<vector<int>>& dp) {
        if (val == 0) {
            return 0;
        }
        if (i >= v.size())
            return 1e5;
        if (dp[i][val] != -1)
            return dp[i][val];
        int take = 1e5;
        int nottake = helper(i + 1, val, v, dp);

        if (v[i].first <= val) {
            int x = helper(i + 1, val - v[i].first, v, dp);

            if (x != 1e5) {
                take = max(v[i].second, x);
            }
        }

        return dp[i][val] = min(take, nottake);
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();

        unordered_map<int, vector<pair<int, int>>> mp;

        for (int i = 0; i < q.size(); i++) {
            for (int j = q[i][0]; j <= q[i][1]; j++) {
                mp[j].push_back({q[i][2], i});
            }
        }
        for (int i = 0; i < n; i++) {
            if (mp.find(i) != mp.end())
                sort(mp[i].begin(), mp[i].end(),
                     [](const pair<int, int>& a, const pair<int, int>& b) {
                         return a.second < b.second;
                     });
        }
        int maxi = -1;
        for (int i = 0; i < n; i++) {

            int val = nums[i];

            if (val == 0)
                continue;
            if (mp.find(i) == mp.end() || mp[i].empty())
                return -1;
            vector<pair<int, int>>& v = mp[i];
            vector<vector<int>> dp(v.size() + 1, vector<int>(val + 1, -1));
            int ans = helper(0, val, v, dp);
            if (ans == 1e5)
                return -1;
            maxi = max(ans, maxi);
        }
        return (maxi==-1 ? 0:maxi + 1);
    }
};

