class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)

        def can_form_zero_array(k):
            coverage = [[] for _ in range(n)]
            for i in range(k):
                l, r, val = queries[i]
                for j in range(l, r + 1):
                    coverage[j].append(val)

            for i in range(n):
                target = nums[i]
                vals = coverage[i]
                if target == 0:
                    continue
                if not vals:
                    return False
                dp = [False] * (target + 1)
                dp[0] = True
                for val in vals:
                    for x in range(target, val - 1, -1):
                        if dp[x - val]:
                            dp[x] = True
                if not dp[target]:
                    return False
            return True

        left, right = 0, len(queries)
        res = -1
        while left <= right:
            mid = (left + right) // 2
            if can_form_zero_array(mid):
                res = mid
                right = mid - 1
            else:
                left = mid + 1

        return res

