class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        start = 1
        end = len(queries)
        ans = -1
        if not self.check(nums, queries):
            return -1
        if all(x == 0 for x in nums):
            return 0
        while start <= end:
            mid = start + (end - start) // 2
            if self.check(nums, queries[:mid]):
                end = mid - 1
                ans = mid
            else:
                start = mid + 1
        return ans

    def check(self, arr, q):
        sweep = [[] for _ in range(len(arr))]
        for i, j, v in q:
            for k in range(i, j+1):
                sweep[k].append(v)
        #print(sweep)
        for i, e in enumerate(arr):
            if not self.can_make_zero(e, sweep[i]):
                return False
        return True

    def can_make_zero(self, e, arr, index=0, memo=None):
        if memo is None:
            memo = {}
        if (e, index) in memo:
            return memo[(e, index)]
        if e == 0:
            return True
        if e < 0 or index >= len(arr):
            return 
        if self.can_make_zero(e, arr, index + 1, memo):
            memo[(e, index)] = True
            return True
        if self.can_make_zero(e - arr[index], arr, index + 1, memo):
            memo[(e, index)] = True
            return True
        memo[(e, index)] = False
        return False

