class Solution(object):
    def minZeroArray(self, nums, queries):
        """
        :type nums: List[int]
        :type queries: List[List[int]]
        :rtype: int
        """
        x = [[] for _ in range(len(nums))]

        for i in range(len(queries)):
            [l, r, val] = queries[i]
            for k in range(l, r + 1):
                x[k].append([val, i])

        def check(index):
            dp = {}

            def BFS(arr, i, sum, target):
                if(sum == target):
                    return i - 1
                if(sum > target):
                    return 1000000000
                if(i == len(arr)):
                    return 1000000000

                if((sum, i) in dp):
                    return dp[(sum, i)]

                ans = min(BFS(arr, i + 1, sum + arr[i], target), BFS(arr, i + 1, sum, target))
                dp[(sum, i)] = ans
                return ans

            arr = []
            for i in x[index]:
                arr.append(i[0])
            target = nums[index]

            res = BFS(arr, 0, 0, target)
            if(res == 1000000000):
                return -1
            else:
                return x[index][res][1] + 1



        ans = 0
        for i in range(len(nums)):
            if(nums[i] != 0):
                index = check(i)
                if(index == -1):
                    return -1
                else:
                    ans = max(ans, index)
        return ans

