class Solution {

    static boolean subsetsum(ArrayList<Integer> arr, int sum) {
        int n = arr.size();
        boolean[] prev = new boolean[sum + 1];
        boolean[] curr = new boolean[sum + 1];

        prev[0] = true;

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= sum; j++) {
                if (j < arr.get(i-1)) {
                    curr[j] = prev[j];
                }
                else {
                    curr[j]
                        = prev[j] || prev[j - arr.get(i-1)];
                }
            }

            System.arraycopy(curr, 0, prev, 0, sum + 1);
        }
        return prev[sum];
    }


    public boolean check(ArrayList<ArrayList<Integer>> al, int[] nums){
        int n = nums.length;


        for(int i=0;i<n;i++){
            ArrayList<Integer> temp = al.get(i);
            Collections.sort(temp);

            if(!subsetsum(temp, nums[i])){
                return false;
            }

        }
        return true;
    }
    public int minZeroArray(int[] nums, int[][] q) {
        int n = nums.length;

        int summ = 0;
        for(int i=0;i<n;i++){
            summ+= nums[i];
        }
        if(summ==0)
        {
            return 0;
        }

        ArrayList<ArrayList<Integer>> al = new ArrayList<ArrayList<Integer>>();
        for(int i=0;i<n;i++){
            al.add(new ArrayList<>());
        }
        for(int i=0;i<q.length;i++){
            int l = q[i][0];
            int r = q[i][1];
            int v = q[i][2];
            for(int j=l;j<=r;j++){
                al.get(j).add(v);
            }

            if(check(al, nums)){
                return i+1;
            }

        }

        return -1;
    }
}

