                def dfs(j, k):
                    if k == 0:
                        return True
                    if j == len(c):
                        return False
                    if dp[j][k] != -1:
                        return dp[j][k]

                    take = dfs(j + 1, k - c[j]) if k >= c[j] else False
                    skip = dfs(j + 1, k)

                    dp[j][k] = take or skip
                    return dp[j][k]

                if not dfs(0, a[i]):
                    ok2 = False
                    break  

            if ok2:
                res = mid 
                right = mid - 1  
            else:
                left = mid + 1 

