#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    bool calc(long long val, vector<int> &has) {
        vector<int> dp(val + 1, 0); // Using vector<int> instead of vector<bool>
        dp[0] = 1;

        for(int num : has) {
            for(long long j = val; j >= num; j--) {
                dp[j] = dp[j] | dp[j - num]; // Fixed bitwise OR assignment
            }
        }
        return dp[val];
    }

    bool isPossible(int mid, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> has(n);

        // Apply queries up to 'mid'
        for(int i = 0; i <= mid; i++) {
            int from = queries[i][0];
            int to = queries[i][1];
            int val = queries[i][2];

            for(int z = from; z <= to; z++) {
                has[z].push_back(val);
            }
        }

        // Check if all nums[z] can be formed
        for(int z = 0; z < n; z++) {
            if (!calc(nums[z], has[z])) return false;
        }

        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int left = 0, right = queries.size() - 1, ans = -1;
         bool alreadyPossible = true;
        for (int z = 0; z < nums.size(); z++) {
            vector<int> empty; // No elements to use
            if (!calc(nums[z], empty)) {
                alreadyPossible = false;
                break;
            }
        }
        if (alreadyPossible) return 0;

        while(left <= right) {
            int mid = left + (right - left) / 2;
            if (isPossible(mid, nums, queries)) {
                ans = mid + 1; // Store potential answer
                right = mid - 1; // Try for a smaller index
            } else {
                left = mid + 1;
            }
        }

        return ans;
    }
};

