class Solution {
public:
    bool check(int sum,vector<int>&vals){
        vector<bool> dp(sum+1,false);
        dp[0]=true;
        for(auto it:vals){
            for(int s=sum;s>=it;s--){
                dp[s]=dp[s]||dp[s-it];
            }
        }
