    bool isPossible(vector<int>& nums, vector<vector<int>>& q,int limit){
        int n=nums.size();
        vector<vector<int>>values(n);
        for(int i=0;i<=limit;i++){
            int l=q[i][0];
            int r=q[i][1];
            int val=q[i][2];
            for(int j=l;j<=r;j++){
                values[j].push_back(val);
            }
        }
        for(int i=0;i<n;i++){
            if(nums[i]==0)continue;
            if(!check(nums[i],values[i]))return false;
        }
        return true;

    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n=nums.size();
        vector<int>temp(n,0);
        if(nums==temp)return 0;
        int low=0;
        int high=q.size()-1;
        int ans=-1;
        while(low<=high){
            int mid=(low+high)/2;
            if(isPossible(nums,q,mid)){
                ans=mid+1;
                high=mid-1;
            }
            else low=mid+1;
        }
        return ans;
    }
};

