import java.util.*;

public class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        List<BitSet> bitSetList = new ArrayList<>(n);

        for (int i = 0; i < n; i++) {
            BitSet bitSet = new BitSet(1001);
            bitSet.set(0);
            bitSetList.add(bitSet);
        }

        boolean allZeros = true;
        for (int num : nums) {
            if (num != 0) {
                allZeros = false;
                break;
            }
        }

        if (allZeros) return 0;

        for (int k = 0; k < queries.length; k++) {
            int left = queries[k][0];
            int right = queries[k][1];
            int value = queries[k][2];

            for (int j = left; j <= right; j++) {
                BitSet current = (BitSet) bitSetList.get(j).clone();
                current.or(shiftLeft(bitSetList.get(j), value));

                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    current.clear(pos);
                }

                bitSetList.set(j, current);
            }

            boolean allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (!bitSetList.get(j).get(nums[j])) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;
        }

        return -1;
    }

    private BitSet shiftLeft(BitSet bitSet, int positions) {
        BitSet result = new BitSet(bitSet.length() + positions);
        for (int i = bitSet.nextSetBit(0); i >= 0; i = bitSet.nextSetBit(i + 1)) {
            result.set(i + positions);
        }
        return result;
    }
}

