class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<int>> queriesForIndices(n);
        for (int i = 0; i < m; ++i) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for (int j = l; j <= r; ++j) {
                queriesForIndices[j].push_back(i);
            }
        }

        vector<int> k_j(n, -1);
        for (int j = 0; j < n; ++j) {
            unordered_set<int> possibleSums;
            possibleSums.insert(0);
            int currentSum = 0;
            int target = nums[j];
            if (target == 0) {
                k_j[j] = 0;
                continue;
            }
            int earliest = -1;
            for (int q : queriesForIndices[j]) {
                int val = queries[q][2];
                unordered_set<int> newSums;
                for (int s : possibleSums) {
                    int newSum = s + val;
                    if (newSum == target) {
                        earliest = q + 1; 
                        break;
                    }
                    if (newSum < target) {
                        newSums.insert(newSum);
                    }
                }
                if (earliest != -1) break;
                possibleSums.insert(newSums.begin(), newSums.end());
            }
            k_j[j] = earliest;
        }

        int max_k = -1;
        for (int j = 0; j < n; ++j) {
            if (k_j[j] == -1) return -1;
            if (k_j[j] > max_k) max_k = k_j[j];
        }
        return max_k;
    }
};

