class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        auto validPrefix = [&](int k) -> bool {

            const int MAXSUM = 1001;
            vector<bitset<MAXSUM>> dp(n), mask(n);

            for (int i = 0; i < n; i++) {
                dp[i].reset();
                dp[i].set(0, true);
                mask[i].reset();

                for (int s = 0; s <= nums[i]; s++) {
                    mask[i].set(s, true);
                }
            }

            for (int j = 0; j < k; j++) {
                int l = queries[j][0];
                int r=queries[j][1];
                int val=queries[j][2];
                for (int i = l; i <= r; i++) {

                    bitset<MAXSUM> shifted = dp[i] << val;

                    shifted &= mask[i];
                    dp[i] |= shifted;
                }
            }
            for (int i = 0; i < n; i++) {
                if (!dp[i].test(nums[i]))
                    return false;
            }
            return true;
        };
        int left = 0,
            right = m + 1; 
        while (left < right) {
            int mid = left + (right - left) / 2;
            if (validPrefix(mid))
                right = mid;
            else
                left = mid + 1;
        }

        return (left > m ? -1 : left);
    }
};

