class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {

       Set<Integer>[] u = new HashSet[nums.length];

       for(int i =0; i<nums.length; i++){
           if(nums[i]>0){
                u[i] = new HashSet<>();
                u[i].add(nums[i]);
           }
       }

        if(allNull(u)) return 0;

        for(int j =0; j<queries.length; j++){

            int[] q = queries[j];

            for(int i = q[0]; i<= q[1]; i++){
                u[i] = apply(u[i], q[2]);
            }
            if(allNull(u)) return j+1;
        }



        return -1;
    }


    boolean allNull(Set<Integer>[] s){
        for(Set<Integer> x: s){
            if(x!=null) return false;
        }
        return true;
    }

    Set<Integer> apply(Set<Integer> os, int x){
        if(os == null ) return null;

        Set<Integer> set = new HashSet<>();

        for( int i : os){
            if(i == x) return null;
            if(i>x)
                set.add(i-x);
            set.add(i);
        }
        return set;

    }
}

