#include <vector>
#include <bitset>
using namespace std;

#define BIT bitset<1001>
#define PB push_back
#define SZ(x) ((int)(x).size())
#define FOR(i, a, b) for (int i = (a); i < (b); i++)
#define REP(i, n) FOR(i, 0, n)
#define RST(x, y) (x).reset(y)
#define SET(x, y) (x).set(y, true)
#define OR(x, y) (x |= y)
#define TST(x, y) (x.test(y))
#define RET return
#define VEC vector
#define INT int

class Solution {
public:
    INT minZeroArray(VEC<INT>& a, VEC<VEC<INT>>& q) {
        INT n = SZ(a);
        VEC<BIT> dp(n);
        ini(dp, n);

        if (chk(a, n)) RET 0;

        REP(k, SZ(q)) {
            upd(dp, q[k], a, n);
            if (chk2(dp, a, n)) RET k + 1;
        }
        RET -1;
    }

private:
    void ini(VEC<BIT>& dp, INT n) {
        REP(j, n) {
            dp[j].reset();
            SET(dp[j], 0);
        }
    }

    bool chk(VEC<INT>& a, INT n) {
        REP(j, n) if (a[j] != 0) RET false;
        RET true;
    }

    void upd(VEC<BIT>& dp, VEC<INT>& q, VEC<INT>& a, INT n) {
        INT l = q[0], r = q[1], v = q[2];
        FOR(j, l, r + 1) {
            OR(dp[j], (dp[j] << v));
            FOR(p, a[j] + 1, 1001) RST(dp[j], p);  // Fix: Reset specific bits, not the whole dp[j]
        }
    }

    bool chk2(VEC<BIT>& dp, VEC<INT>& a, INT n) {
        REP(j, n) if (!TST(dp[j], a[j])) RET false;
        RET true;
    }
};

